import axios from 'axios';

export function setCurrent(data) {
  return {
    type: 'SET_CURRENT',
    data,
  };
}

export function load(data) {
  return {
    type: 'LOAD',
    data,
  };
}

export function update(data) {
  return {
    type: 'UPDATE',
    data,
  };
}

export function del(data) {
  return {
    type: 'DELETE',
    data,
  };
}

export function upload(data) {
  return {
    type: 'UPLOAD',
    data,
  };
}

export function resetCurrentRestaurant(data) {
  return {
    type: 'RESET_CURRENT',
    data,
  };
}
export function changeCurrentRestaurant(data) {
  return {
    type: 'CHANGE_CURRENT',
    data,
  };
}

export function fetchRestaurants() {
  return function(dispatch) {
    return axios.get("api/restaurants/")
      .then(({ data }) => {
      dispatch(load(data));
    });
  };
}

export function updateRestaurant(restaurant) {
  return function(dispatch) {
    return axios.get("api/restaurants/")
      .then(() => {
      dispatch(update({ data: restaurant }));
    });
  };
}

export function deleteRestaurant(id) {
  return function(dispatch) {
    return axios.delete(`api/restaurants/${id}`)
      .then(() => {
      dispatch(del({ data: id }));
    });
  };
}

export function uploadRestaurants(file) {
  console.log(':::::::::::::::::::::::',file)
  return function(dispatch) {
    return axios.post("api/restaurants/upload",
    { file },
    { data: {file},
       headers: { 'Content-Type': 'application/x-www-form-urlencoded' }}
    ).then(({ data }) => {
      dispatch(load(data));
    });
  };
}
