import React from 'react';
// import google from 'googleapis';
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from 'react-places-autocomplete';
import { locationToAddress } from '../utils/location'

class LocationSearchInput extends React.Component {

  // componentDidMount() {
  //   locationToAddress(this.props.location)
  //   .then((address) => this.setState({ address }));
  // }

  handleChange = address => {
    console.log('adress:', address);
    // this.setState({ address });
    this.props.onLocationChange(address);
  };

  handleSelect = address => {
    this.props.onLocationChange(address);
    // this.setState({ address });
    // geocodeByAddress(address)
    //   .then(results => getLatLng(results[0]))
    //   .then(latLng =>{console.log('important:', latLng); this.props.onLocationChange(`${latLng.lat},${latLng.lng}`)})
  };

  render() {
    return (
      <PlacesAutocomplete
        value={this.props.address}
        onChange={(address) => this.props.onLocationChange(address)}
        onSelect={(address) => this.props.onLocationChange(address)}
      >
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div>
            <input
              {...getInputProps({
                placeholder: 'Search Places ...',
                className: 'location-search-input',
              })}
            />
            <div className="autocomplete-dropdown-container">
              {loading && <div>Loading...</div>}
              {suggestions.map(suggestion => {
                const className = suggestion.active
                  ? 'suggestion-item--active'
                  : 'suggestion-item';
                // inline style for demonstration purpose
                const style = suggestion.active
                  ? { backgroundColor: '#fafafa', cursor: 'pointer' }
                  : { backgroundColor: '#ffffff', cursor: 'pointer' };
                return (
                  <div
                    {...getSuggestionItemProps(suggestion, {
                      className,
                      style,
                    })}
                  >
                    <span>{suggestion.description}</span>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </PlacesAutocomplete>
    );
  }
}

export default LocationSearchInput;
