import axios from 'axios';

export function load(data) {
  return {
    type: 'LOAD',
    payload: data
  };
}

export function fetchRestaurants() {
  return function(dispatch) {
    return axios.get("http://localhost/3002/api/restaurants/")
      .then(({ data }) => {
      dispatch(load(data));
    });
  };
}
