import axios from 'axios';
import { parseData, AddressToLatLng } from './utils/parser'

export function setCurrent(data) {
  return {
    type: 'SET_CURRENT',
    data,
  };
}

export function load(data) {
  return {
    type: 'LOAD',
    data,
  };
}

export function update(data) {
  return {
    type: 'UPDATE',
    data,
  };
}

export function del(data) {
  return {
    type: 'DELETE',
    data,
  };
}

export function resetCurrentRestaurant(data) {
  return {
    type: 'RESET_CURRENT',
    data,
  };
}
export function changeCurrentRestaurant(data) {
  return {
    type: 'CHANGE_CURRENT',
    data,
  };
}

export function fetchRestaurants() {
  return function(dispatch) {
    return axios.get("api/restaurants/")
      .then(({ data }) => {
      dispatch(load(data));
    });
  };
}

export function updateRestaurant(restaurant) {
  return async function(dispatch) {
    restaurant.Location = await addressToLatLng(restaurant.address);
    return axios.put(`api/restaurants/${restaurant.Id}`, restaurant)
      .then(() => {
        console.log('data:::::',restaurant)
      dispatch(update({ restaurant }));
    });
  };
}

export function deleteRestaurant(restaurant) {
  return function(dispatch) {
    return axios.delete(`api/restaurants/${restaurant.Id}`)
      .then(() => {
      dispatch(del({ Id: restaurant.Id }));
    });
  };
}

export function uploadRestaurants(file) {
  return function(dispatch) {
    const data = new FormData();
    data.append('file', file);
    return axios.post("api/restaurants/upload", data)
    .then(({ data }) => {
      return parseData(data)
      .then((data) => {
        dispatch(load(data));
      })
    });
  };
}
