import React from "react";
import { connect } from "react-redux";
import { Table } from 'react-bootstrap';
// import { getrestaurents } from "../redux/selectors";
// import { getrestaurentsByVisibilityFilter } from "../redux/selectors";
// import { VISIBILITY_FILTERS } from "../constants";

const restaurentList = ({ restaurents }) => (
  <ul className="restaurent-list">
    {restaurents && restaurents.length
      ? restaurents.map((restaurent, index) => {
          return <restaurent key={`restaurent-${restaurent.id}`} restaurent={restaurent} />;
        })
      : "No restaurents"}
  </ul>
);

class restaurentsTable extends React.Component {
  render() {
    return (
      <Table data={ restaurents } options={ { noDataText: 'No Restaurents' } }>
        <TableHeaderColumn dataField='id' isKey>ID</TableHeaderColumn>
        <TableHeaderColumn dataField='name'>Name</TableHeaderColumn>
        <TableHeaderColumn dataField='type'>Type</TableHeaderColumn>
        <TableHeaderColumn dataField='phone'>Phone</TableHeaderColumn>
        <TableHeaderColumn dataField='location'>Location</TableHeaderColumn>
      </Table>
    );
  }
}

