// );

class restaurantsTable extends React.Component {

  componentDidMount() {
    this.props.onFetchRestaurants();
  }

  render() {
    console.log('%%%%%%%%',this.props.restaurants);
    return (
      <BootstrapTable data={ this.props.restaurants } options={ { noDataText: 'No Restaurents' } }>
        <TableHeaderColumn dataField='Id' isKey>ID</TableHeaderColumn>
        <TableHeaderColumn dataField='Name'>Name</TableHeaderColumn>
        <TableHeaderColumn dataField='Type'>Type</TableHeaderColumn>
        <TableHeaderColumn dataField='Phone'>Phone</TableHeaderColumn>
        <TableHeaderColumn dataField='Location'>Location</TableHeaderColumn>
      </BootstrapTable>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('@@@@@@@@@@@@',state)
  const restaurants = state.restaurants || { data: [] };
  return { restaurants }
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFetchRestaurants: () => {
      dispatch(fetchRestaurants());
    }
  };
};
// export default restaurentList;
export default connect(mapStateToProps, mapDispatchToProps)(restaurantsTable);
