import React from "react";
import { connect } from "react-redux";
import { uploadRestaurants } from '../actions'

class Uploader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fileName: '',
      file: {},
    }
  }

  onFileChange(event) {
    console.log('{{{{{{{', event.target.value, this)
    this.setState({
      fileName: event.target.value,
      file: event.target.files
    })
  }

  render() {
    console.log('props:::::',this.props)
    return (
      <div>
        <input
          className="restaurant-input"
          type="file"
          value={this.state.fileName}
          onChange={this.onFileChange.bind(this)}
        />
        <input
          type="button"
          value="Upload"
          onClick={() => this.props.onUpload(this.state.file)}
        />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('@@@@@@@@@@@@',state)
  const restaurants = state.restaurants || { data: [] };
  return { restaurants }
};

const mapDispatchToProps = (dispatch) => {
  return {
    onUpload: (file) => {
      console.log('dddddd', file)
      dispatch(uploadRestaurants(file))
    },
  };
};
// export default restaurantList;
export default connect(mapStateToProps, mapDispatchToProps)(Uploader);
