import { combineReducers, applyMiddleware, createStore } from 'redux'
// import { setAxiosConfig, reducer as restaurants } from 'redux-json-api';
import thunk from 'redux-thunk';

function visibilityFilter(state = '*', action) {
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER':
      return action.filter
    default:
      return state
  }
}

function current(state = {}, action) {
  switch (action.type) {
    case 'SET_CURRENT':
      return action.restaurent
    default:
      return state
  }
}

function restaurants(state = [], action) {
  switch (action.type) {
    case 'DELETE':
      return state.filter(r => r.id !== action.id);
    case 'UPDATE':
      return state.map((r) => r.id === action.restaurent.id ? action.restaurent : r);
    case 'UPLOAD':
      return state.push(...action.restaurents);
      case 'LOAD':
        return action.data;
    default:
      return state
  }
}

const reducer = combineReducers({ visibilityFilter, current, restaurants })
const store = createStore(reducer, applyMiddleware(thunk))

export default store
