import React from "react";
import { connect } from "react-redux";
import { updateRestaurant, deleteRestaurant, resetCurrentRestaurant, changeCurrentRestaurant } from '../actions'

class RestaurantEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = {}
  }

  onNameChange(event) {
    this.props.onChange
    console.log('{{{{{{{', event.target.value, this)
    this.setState({...this.state, Name: event.target.value })
  }

  onTypeChange(event) {
    console.log('{{{{{{{', event.target.value, this)
    this.setState({...this.state, Type: event.target.value })
  }

  onPhoneChange(event) {
    console.log('{{{{{{{', event.target.value, this)
    this.setState({...this.state, Phone: event.target.value })
  }

  onLocationChange(event) {
    console.log('{{{{{{{', event.target.value, this)
    this.setState({...this.state, Location: event.target.value })
  }

  render() {
    if(!this.props.restaurant) return '';
    console.log('props:::::',this.props)
    return (
      <div>
      <form>
        <h3>Restaurant Editor</h3>
        <p>
          <label>Name</label>
          <input
            value={this.props.restaurant.Name}
            onChange={this.onNameChange.bind(this)}
          />
        </p>
        <p>
          <label>Type</label>
          <input
            value={this.props.restaurant.Type}
            onChange={this.onTypeChange.bind(this)}
          />
        </p>
        <p>
          <label>Phone</label>
          <input
            value={this.props.restaurant.Phone}
            onChange={(event) => this.onPropChange({ Phone: event.target.value})}
          />
        </p>
        <p>
          <label>Location</label>
          <input
            value={this.props.restaurant.Location}
            onChange={(event) => this.onPropChange({ Location: event.target.value})}
          />
        </p>
        <button onClick={this.onUpdate}>Save</button>
        <button onClick={this.onDelete}>Delete</button>
        <button onClick={this.onCancel}>Cancel</button>
      </form>
    </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { restaurant: state.current }
};

const mapDispatchToProps = (dispatch) => {
  return {
    onUpdate: (restaurant) => {
      console.log('dddddd', restaurant)
      dispatch(updateRestaurant(restaurant))
    },
    onDelete: (restaurant) => {
      console.log('dddddd', restaurant)
      dispatch(deleteRestaurant(restaurant))
    },
    onCancel: (restaurant) => {
      console.log('dddddd', restaurant)
      dispatch(resetCurrentRestaurant(restaurant))
    },
    onPropChange: (prop) => {
      console.log('dddddd', restaurant)
      dispatch(changeCurrentRestaurant(prop))
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RestaurantEditor);
